import React from 'react';
import { FiSearch, FiRefreshCcw } from 'react-icons/fi';

const FilterBar = ({ searchTerm, handleSearchChange, filters, handleFilterChange, clearFilters }) => {
  return (
    <div className="bg-white shadow-md rounded-lg p-6 mb-6 flex flex-col sm:flex-row justify-between items-center">
      {/* Search Box */}
      <div className="flex items-center space-x-2 w-full sm:w-1/2 mb-4 sm:mb-0">
        <FiSearch className="text-gray-500" />
        <input
          type="text"
          value={searchTerm}
          onChange={handleSearchChange}
          placeholder="Search by job title"
          className="border rounded-md p-3 w-full"
        />
      </div>

      {/* Most Recent Filter */}
      <div className="flex items-center space-x-4 w-full sm:w-1/3 mb-4 sm:mb-0">
        <select
          name="sortBy"
          value={filters.sortBy}
          onChange={handleFilterChange}
          className="border rounded-md p-3 w-full"
        >
          <option value="mostRecent">Most Recent</option>
        </select>

        {/* Clear Filters */}
        <button
          onClick={clearFilters}
          className="bg-gray-200 text-gray-700 px-4 py-2 rounded-md flex items-center space-x-2 hover:bg-gray-300"
        >
          <FiRefreshCcw />
          <span>Clear Filters</span>
        </button>
      </div>
    </div>
  );
};

export default FilterBar;
