import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import FilterBar from '../components/FilterBar';
import JobList from '../components/JobList';
import axios from 'axios';

const Home = () => {
  const [jobs, setJobs] = useState([]);
  const [filteredJobs, setFilteredJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({
    jobType: '',
    isRemote: null,
    salaryRange: '',
  });

  useEffect(() => {
    axios.get('http://localhost:8000/api/jobs/')
      .then((response) => {
        setJobs(response.data);
        setFilteredJobs(response.data);
        setLoading(false);
      })
      .catch((err) => {
        setError('Failed to fetch jobs.');
        setLoading(false);
      });
  }, []);

  useEffect(() => {
    // Filter jobs based on search term and filters
    let tempJobs = [...jobs];
    
    if (searchTerm) {
      tempJobs = tempJobs.filter(job => 
        job.title.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }
    
    if (filters.jobType) {
      tempJobs = tempJobs.filter(job => job.employment_type === filters.jobType);
    }

    if (filters.isRemote !== null) {
      tempJobs = tempJobs.filter(job => job.is_remote === filters.isRemote);
    }

    if (filters.salaryRange) {
      tempJobs = tempJobs.filter(job => job.salary === filters.salaryRange);
    }

    setFilteredJobs(tempJobs);
  }, [searchTerm, filters, jobs]);

  const handleSearchChange = (e) => setSearchTerm(e.target.value);
  
  const handleFilterChange = (e) => {
    setFilters({
      ...filters,
      [e.target.name]: e.target.value,
    });
  };

  if (loading) {
    return <div className="text-center text-2xl font-semibold">Loading...</div>;
  }

  if (error) {
    return <div className="text-center text-red-500">{error}</div>;
  }

  return (
    <div className="font-sans bg-gray-100">
      <Navbar />
      <div className="container mx-auto py-6">
        <FilterBar 
          searchTerm={searchTerm}
          handleSearchChange={handleSearchChange}
          filters={filters}
          handleFilterChange={handleFilterChange}
        />
        <JobList jobs={filteredJobs} />
      </div>
    </div>
  );
};

export default Home;
