import React from 'react';
import { FiGlobe, FiClock } from 'react-icons/fi';

const JobCard = ({ job }) => {
  return (
    <div className="bg-cardBackground shadow-md rounded-md p-4 mb-4">
      <div className="flex items-center">
        <img src={job.logo} alt="Company Logo" className="h-12 w-12 mr-4" />
        <div>
          <h3 className="text-lg font-bold text-textPrimary">{job.title}</h3>
          <p className="text-sm text-textSecondary">{job.company}</p>
        </div>
      </div>
      <div className="mt-4 flex justify-between">
        <span className="text-textPrimary">
          {job.salary} USD/year
        </span>
        <span className="flex items-center text-textSecondary">
          <FiGlobe className="mr-2" />
          {job.location}
        </span>
        <span className="flex items-center text-textSecondary">
          <FiClock className="mr-2" />
          {job.type}
        </span>
      </div>
    </div>
  );
};

export default JobCard;
