import React from 'react';

const FilterBar = ({ searchTerm, handleSearchChange, filters, handleFilterChange }) => {
  return (
    <div className="bg-white shadow-md rounded-lg p-6 mb-6">
      <div className="flex flex-col sm:flex-row sm:space-x-4">
        {/* Search Box */}
        <input
          type="text"
          value={searchTerm}
          onChange={handleSearchChange}
          placeholder="Search by job title"
          className="border rounded-md p-3 w-full sm:w-1/3 mb-4 sm:mb-0"
        />
        
        {/* Job Type Filter */}
        <select
          name="jobType"
          value={filters.jobType}
          onChange={handleFilterChange}
          className="border rounded-md p-3 w-full sm:w-1/4 mb-4 sm:mb-0"
        >
          <option value="">Select Job Type</option>
          <option value="Full-time">Full-time</option>
          <option value="Part-time">Part-time</option>
          <option value="Contract">Contract</option>
        </select>

        {/* Remote Filter */}
        <select
          name="isRemote"
          value={filters.isRemote}
          onChange={handleFilterChange}
          className="border rounded-md p-3 w-full sm:w-1/4 mb-4 sm:mb-0"
        >
          <option value={null}>Select Remote</option>
          <option value={true}>Remote</option>
          <option value={false}>On-site</option>
        </select>

        {/* Salary Filter */}
        <select
          name="salaryRange"
          value={filters.salaryRange}
          onChange={handleFilterChange}
          className="border rounded-md p-3 w-full sm:w-1/4 mb-4 sm:mb-0"
        >
          <option value="">Select Salary</option>
          <option value="Depends on Experience">Depends on Experience</option>
          <option value="Not Disclosed">Not Disclosed</option>
          <option value="Negotiable">Negotiable</option>
        </select>
      </div>
    </div>
  );
};

export default FilterBar;
