import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import FilterBar from '../components/FilterBar';
import JobList from '../components/JobList';
import axios from 'axios';  // Add axios for HTTP requests

const Home = () => {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);  // To handle loading state
  const [error, setError] = useState(null);  // To handle errors

  useEffect(() => {
    // Make the GET request to your Django API endpoint
    axios.get('http://localhost:8000/api/jobs/') // Replace with your actual Django API URL
      .then((response) => {
        setJobs(response.data);  // Set the fetched jobs data
        setLoading(false);  // Set loading to false when data is fetched
      })
      .catch((err) => {
        setError('Failed to fetch jobs.');  // Handle any errors
        setLoading(false);  // Set loading to false in case of error
      });
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <Navbar />
      <FilterBar />
      <JobList jobs={jobs} />
    </div>
  );
};

export default Home;
