import React from 'react';
import { FiBriefcase, FiMapPin, FiDollarSign, FiGlobe } from 'react-icons/fi';

const JobCard = ({ job }) => {
  return (
    <div className="bg-white shadow-lg rounded-lg p-6 mb-6 hover:shadow-xl transition-shadow duration-300 max-w-lg mx-auto">
      {/* Company Info */}
      <div className="flex items-center mb-4">
        <img
          src={job.company_logo_url || 'https://via.placeholder.com/50'}
          alt="Company Logo"
          className="h-12 w-12 mr-4 rounded-full object-cover"
        />
        <div>
          <h3 className="text-xl font-semibold text-gray-800">{job.title || 'Job Title Not Provided'}</h3>
          <p className="text-md text-gray-500">{job.company_name || 'Company Name Not Provided'}</p>
        </div>
      </div>

      {/* Job Details */}
      <div className="flex flex-wrap space-x-4 text-gray-600 mb-4">
        <div className="flex items-center">
          <FiBriefcase className="mr-2" />
          <span>{job.employment_type || 'Employment Type Not Provided'}</span>
        </div>
        <div className="flex items-center">
          <FiMapPin className="mr-2" />
          <span>{job.location || 'Location Not Provided'}</span>
        </div>
        {job.work_from_home && (
          <div className="flex items-center">
            <FiGlobe className="mr-2" />
            <span>Work from Home Available</span>
          </div>
        )}
      </div>

      {/* Job Description */}
      <div className="mb-4">
        <p className="text-gray-600 text-sm line-clamp-3">
          {job.summary || 'Job summary not provided. Visit the job link for more details.'}
        </p>
        <a
          href={job.details_page_url || '#'}
          target="_blank"
          rel="noopener noreferrer"
          className="text-blue-500 hover:text-blue-700 text-sm mt-2 block"
        >
          {job.details_page_url ? 'Read Full Job Description' : 'No Job Description Available'}
        </a>
      </div>

      {/* Salary & Sponsorship */}
      <div className="flex items-center space-x-4">
        <div className="flex items-center text-gray-800">
          <FiDollarSign className="mr-2" />
          <span>{job.salary || 'Salary Not Disclosed'}</span>
        </div>
        <div className="text-sm text-gray-600">
          {job.willing_to_sponsor ? 'Visa Sponsorship Available' : 'No Visa Sponsorship'}
        </div>
      </div>
    </div>
  );
};

export default JobCard;
